INSERT INTO `tintuc` (`matintuc`, `manhanvien`, `tieude`, `tomtac`, `noidung`, `ngaydang`, `anhtieude`, `trangthai`) VALUES
(13, 'NV0002', 'Ra quân công trình tuyến hẻm an toàn, mỹ quan, tiết kiệm', 'Sáng ngày 01/03/2015, Đoàn Thanh niên Tổng công ty đã cùng với Quận Đoàn 6, Quận Đoàn 2 tổ chức các hoạt động hưởng ứng sau Lễ ra quân Tháng Thanh niên năm 2015 với chủ đề “Tự hào tiến bước dưới cờ Đảng”. Các hoạt động hướng đến chào mừng 40 năm Ngày giải phóng hoàn toàn miền Nam thống nhất đất nước (30/4/1975 - 30/4/2015); chào mừng Đại hội Đại biểu Đảng bộ Tổng công ty lần thứ II (nhiệm kỳ 2015-2020). ', '<p><br />\n&nbsp;</p>\n\n<p><span style="font-size:16px"><span style="font-family:times new roman,times,serif">Công trình thanh niên “40 Tuyến hẻm có hệ thống điện an toàn, mỹ quan, tiết kiệm” trên địa bàn Thành phố được triển khai thực hiện với mục đích phát huy chuyên môn, tinh thần xung kích, tình nguyện của Đoàn viên tham gia cùng giải quyết những vấn đề khó khăn của địa phương là xử lý các điểm mất an toàn trong hệ thống đèn chiếu sáng dân lập. Các bạn Đoàn viên đã tổ chức thi công lắp đặt mới hệ thống đèn chiếu sáng dân lập, lắp đèn compact tiết kiệm điện, làm sạch, xóa quảng cáo, sơn số trụ, thực hiện tư vấn sử dụng điện an toàn, tiết kiệm...</span></span></p>\n\n<p><span style="font-size:16px"><span style="font-family:times new roman,times,serif">Bốn tuyến hẻm được thực hiện trong ngày ra quân gồm: hẻm 109 đường Nguyễn Văn Luông – P.10, hẻm 287 đường Nguyễn Văn Luông – P.12- Q.6; Hẻm 162 đường Số 42– P. Bình Trưng Đông, hẻm 128 đường Số 6 – P.Bình Trưng Đông- Q.2. Với tổng chiều dài thực hiện là 1070 mét, lắp mới 56 bộ đèn Compact tiết kiệm điện, các bộ đóng cắt tự động và sau khi thi công tác hệ thống được đấu vào đồng hồ đo đếm và bàn giao lại cho chính quyền địa phương quản lý. Tổng kinh phí thực hiện là hơn 50 triệu đồng. Các tuyến hẻm còn lại sẽ được triển khai thực hiện trước ngày 30/4/2015 trên toàn địa bàn Thành phố.</span></span></p>\n\n<p><span style="font-size:16px"><span style="font-family:times new roman,times,serif">Đây là hoạt động hết sức ý nghĩa và thiết thực của Đoàn Thanh niên TCT trong ngày khởi động Tháng Thanh niên được người dân đồng tình đón nhận. Và được biết trong Tháng Thanh niên này, Đoàn TCT sẽ đồng loạt ra quân các công trình xung kích tuyên truyền tiết kiệm điện như: Nguồn sáng an toàn văn minh tiết kiệm; Tuyến đường kiểu mẫu tiết kiệm điện; Cùng bạn sử dụng điện an toàn, tiết kiệm; các hoạt động Hưởng ứng Giờ Trái Đất 2015...</span></span></p>\n\n<p><span style="font-size:16px"><span style="font-family:times new roman,times,serif">Chúc cho các hoạt động Tháng thanh niên 2015 của Đoàn TCT sẽ thu hút được nhiều Đoàn viên thanh niên tham gia hơn và đạt được thành công rực rỡ, thêm được những công trình, sản phẩm hiệu quả cho người dân Thành phố.</span></span></p>\n\n<p><span style="font-size:16px"><span style="font-family:times new roman,times,serif">Một số hình ảnh của ngày ra quân Tháng Thanh niên:</span></span></p>\n\n<p style="text-align:center"><span style="font-size:16px"><span style="font-family:times new roman,times,serif"><img alt="" src="http://www.hcmpc.com.vn/ckfinder/userfiles/images/TongCongTy/Cac%20su%20kien/raquan-thangthanhnien2015%20%281%29.jpg" style="height:375px; width:500px" /><br />\n<span style="font-size:14px"><em>Đoàn TCT ra quân tại Lễ khởi động Tháng Thanh niên năm 2015 do Thành Đoàn TP.HCM phát động<br />\n&nbsp;<img alt="" src="http://www.hcmpc.com.vn/ckfinder/userfiles/images/TongCongTy/Cac%20su%20kien/raquan-thangthanhnien2015%20%282%29.jpg" style="height:281px; width:500px" /><br />\nĐ/c Nguyễn Văn Rảnh – Ủy viên Ban Thường vụ, Trưởng ban Dân vận Thành ủy TP. HCM và đ/c Nguyễn Mạnh Cường – Bí thư Thành Đoàn đến thăm hỏi các bạn ĐVTN thực hiện công trình<br />\n&nbsp;<img alt="" src="http://www.hcmpc.com.vn/ckfinder/userfiles/images/TongCongTy/Cac%20su%20kien/raquan-thangthanhnien2015%20%283%29.jpg" style="height:334px; width:500px" /><br />\nThực hiện lắp đặt hệ thống đèn chiếu sáng dân lập tại tuyến hẻm 109 đường Nguyễn Văn Luông – P.10 – Quận 6</em></span></span></span></p>\n\n<p><span style="font-size:16px"><span style="font-family:times new roman,times,serif"><strong>Nguyễn Tấn Hưng – Bí thư Đoàn TCT</strong></span></span></p>\n', '2015-03-20', 'Untitled.png', 1),
(14, 'NV0001', 'Gặp người phụ nữ từng “cố thủ” trên cây phản đối chặt hạ', 'Hai người phụ nữ ở quận Long Biên (Hà Nội) mang bánh mì, nước uống trèo lên cây xoài trước cửa nhà “cố thủ” nhiều giờ liền để phản đối cơ quan chức năng chặt bỏ xây xanh...', '<div style="text-align: justify;">Theo kế hoạch, trong năm 2015, Hà Nội sẽ chặt hạ, thay thế 6.700 cây xanh trên nhiều tuyến phố, khiến nhiều người không khỏi xót xa, tiếc nuối. Cùng câu chuyện về cây xanh, 2 năm về trước (2013), vì muốn bảo vệ cây, không cho chặt hạ, 2 người phụ nữ ở số nhà 64 đường Ngô Gia Tự (quận Long Biên, Hà Nội) đã trèo lên cây “cố thủ” nhiều giờ liền.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;"><strong>Trèo lên ngọn cây ngăn cản chặt bỏ</strong></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;">Ngày 19.3, chúng tôi đã tìm gặp 2 người phụ nữ trong câu chuyện trên. Bà Nguyễn Thị Thanh (40 tuổi, người từng trèo lên cây “cố thủ” phản đối cơ quan chức năng chặt cây) kể lại: Khoảng 8h40 sáng 8.10.2013, cơ quan chức năng phường Bồ Đề đã đưa xe cẩu, hàng rào sắt đến trước cửa nhà bà để chặt hạ cây xoài. Tuy nhiên, gia đình bà Thanh không đồng ý.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: center;"><img alt="" src="http://24h-static.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://24h-static.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="Gặp người phụ nữ từng “cố thủ” trên cây phản đối chặt hạ - 1" class="news-image" id="news-image-id-0" src="http://24h-img.24hstatic.com/upload/1-2015/images/2015-03-19/1426767143-hkyx1_rvet.jpg" />\r\n<div>Hai người phụ nữ trèo lên cây “cố thủ” phản đối việc chặt hạ cây xoài trước nhà năm 2013. Ảnh: Báo Người Lao Động</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;">“Lúc đó có khoảng 50 người gồm công an, cơ động, cảnh sát giao thông cùng đến hỗ trợ công nhân chặt bỏ cây xoài. Tuy nhiên, cả gia đình tôi đã phản đối kịch liệt. Cuối cùng không còn cách nào khác, hai chị em chúng tôi buộc phải mang bánh mì, nước uống trèo từ mái nhà lên cây “cố thủ” để phản đối”, bà Thanh nói.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;">Bà Thanh cho biết, lý do khiến gia đình bà phản đối việc chặt hạ là vì cây xoài được gia đình bà trồng, chăm sóc từ rất lâu, không có sâu mọt. Thêm nữa, cây xanh mang lại bóng mát cho gia đình.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;">“Để trồng được cây xoài cao hơn 10m, thân 40cm như bây giờ, gia đình tôi đã bỏ công chăm sóc hơn 30 năm nay. Khi đến chặt cây, cơ quan chức năng chỉ nói cây xoài này phải thay thế bằng một cây khác cho đồng bộ tuyến đường. Chúng tôi thấy lý do đó không thuyết phục, nên nhất quyết phản đối”, bà Thanh chia sẻ.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: center;"><img alt="" src="http://24h-static.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://24h-static.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="Gặp người phụ nữ từng “cố thủ” trên cây phản đối chặt hạ - 2" class="news-image" id="news-image-id-1" src="http://24h-img.24hstatic.com/upload/1-2015/images/2015-03-19/1426767143-nnlg6_rxja.jpg" />\r\n<div>Bà Nguyễn Thị Thanh kể lại câu chuyện trèo cây “cố thủ” phản đối việc chặt hạ.</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;">Bà Thanh cho biết thêm, bà cùng với chị gái là bà Đặng Thị Ngát (42 tuổi) vì muốn bảo vệ cây của gia đình nên mới chọn giải pháp như vậy, chứ không có ý chống đối cơ quan chức năng. Thời điểm xảy ra sự việc, người dân hiếu kỳ đã tập trung tại tuyến đường Ngô Gia Tự đông nên gây ùn tắc. Cuối cùng đến 11h30 ngày 8.10.2013, trước sức ép của gia đình, người dân xung quanh, lực lượng chức năng mới chịu rút về và từ bỏ ý định chặt cây xoài.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;">“Kể từ đó đến nay chúng tôi không thấy cơ quan chức năng đến đòi chặt cây nữa. Hiện tại cây xoài đang xanh tốt, có tán lá sum sê”, bà Thanh nói thêm.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: center;"><img alt="" src="http://24h-static.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://24h-static.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="Gặp người phụ nữ từng “cố thủ” trên cây phản đối chặt hạ - 3" class="news-image" id="news-image-id-2" src="http://24h-img.24hstatic.com/upload/1-2015/images/2015-03-19/1426767144-wewe3_gugm.jpg" />\r\n<div>Hiện nay cây xoài trước cửa nhà bà Thanh có độ tuổi khoảng 30 năm, có tán sum sê.</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;"><strong>Cây xanh "chặn đứng" tai nạn chết người</strong></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;">Theo bà Thanh, hiện giờ cây xoài trước cửa nhà bà không cong, sâu mọt, không gây nguy hiểm cho người tham gia giao thông. Vào mùa hè, tán cây tỏa bóng mát cho cả gia đình, đặc biệt mỗi khi người dân ghé quán nước ngồi đều cảm thấy dễ chịu vì có bóng mát.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;">“Năm 2005, xe ô tô chở rác chạy hướng từ cầu Chương Dương sang đường Ngô Gia Tự đã đâm vào nhà dân ở cạnh nhà tôi. Cũng may, thời điểm đó có cây bàng trước cửa đỡ lại chứ không người dân ngủ trong nhà đã nguy hiểm đến tính mạng. Nhà tôi cũng vậy, cũng mong muốn giữ lại cây xoài để đề phòng sự cố tai nạn bất trắc có thể xảy ra”, bà Thanh nói.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;">Cùng thời điểm năm 2013, gia đình bà Đặng Thị Thu (50 tuổi, ở đường Ngô Gia Tự) cũng vô cùng buồn bã khi cây bàng trồng hơn 30 tuổi bị chặt hạ.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: center;"><img alt="" src="http://24h-static.24hstatic.com/images/2014/share-fb.gif" style="height:20px; width:67px" />&nbsp;<img alt="" src="http://24h-static.24hstatic.com/images/2014/share-gg.gif" style="height:20px; width:67px" /><img alt="Gặp người phụ nữ từng “cố thủ” trên cây phản đối chặt hạ - 4" class="news-image" id="news-image-id-3" src="http://24h-img.24hstatic.com/upload/1-2015/images/2015-03-19/1426767144-scah5_djvb.jpg" />\r\n<div>Năm 2013, những cây xanh thân to trên tuyến đường Ngô Gia Tự được chặt hạ, thay thế trồng mới bằng cây phượng. Hiện những cây trên tuyến đường này có thân nhỏ, tán nhỏ, ít bóng mát.</div>\r\n</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;">“Cây xanh ở đường Ngô Gia Tự đều có độ tuổi vài năm đến vài chục năm. Trước thời điểm cây xanh chưa bị chặt hạ, người dân đi qua tuyến đường này cảm thấy rợp bóng mát. Nhưng hiện giờ, tuyến đường này được trồng thay thế bằng cây phượng, rất ít bóng mát. Trồng hơn 2 năm, nhưng cây vẫn chưa có tán rộng, thậm chí một số cây héo dần rồi bị chết”, bà Thu nói.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;">Bà Thu cho hay, gần đây bà có xem trên tivi thấy thông tin Hà Nội sẽ chặt hạ 6.700 cây xanh. Nghe tin, bà Thu đã không khỏi tiếc nuối.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;">“Cây xanh là "lá phổi xanh" của Thủ đô. Chặt cây, đồng nghĩa với việc ô nhiễm môi trường có thể sẽ tăng lên. Tôi không nghĩ cơ quan chức năng lại đi chặt những cây đang tràn đầy sức sống, có tán lá sum sê. Người dân chúng tôi càng nghĩ càng thấy xót xa”, bà Thu chia sẻ.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="text-align: justify;">\r\n<div class="text-embed">Theo thông tin từ Sở Xây dựng Hà Nội, trong năm 2015 sẽ thay thế 6.700 cây xanh, trong đó 4.500 cây xanh không đúng chủng loại (cây cấm trồng) trên 190 tuyến phố; 2.200 cây cong, nghiêng, nguy hiểm, cây cong xấu, cản trở giao thông, ảnh hưởng mỹ quan đô thị cũng cần thay thế. Đặc biệt, những cây gắn bó với Hà Nội như: Dâu da, vông, dướng, trứng cá, bông gòn… thuộc diện cây cấm trồng và sẽ bị thay thế.</div>\r\n</div>\r\n', '2015-03-20', 'Untitled.png', 1),
(15, 'NV0002', 'EMAIL HÓA ĐƠN ĐIỆN TỬ', 'Nhằm nâng cao hiệu quả chăm sóc khách hàng và tạo điều kiện thuận lợi cho khách hàng trong việc xem và nhận HĐĐT, Trung tâm Chăm sóc khách hàng đã triển khai áp dụng hình thức “Tự động gửi Hóa đơn điện tử qua địa chỉ Email của khách hàng” từ tháng 5/2014.', '<p>Nhằm nâng cao hiệu quả chăm sóc khách hàng và tạo điều kiện thuận lợi cho khách hàng trong việc xem và nhận HĐĐT, Trung tâm Chăm sóc khách hàng đã triển khai áp dụng hình thức “Tự động gửi Hóa đơn điện tử qua địa chỉ Email của khách hàng” từ tháng 5/2014. Khách hàng chỉ cần đăng ký một lần duy nhất và sẽ được nhận HĐĐT ngay sau mỗi lần khách hàng thanh toán tiền điện.&nbsp;</p>\r\n\r\n<p>Đến nay, sau hơn nửa năm triển khai, ý tưởng này đã được triển khai tốt trong thực tiễn sản xuất kinh doanh: Đến cuối tháng 12/2014 đã có gần 42 Ngàn khách hàng đăng ký địa chỉ Email nhận HĐĐT tự động và Trung tâm Chăm sóc khách hàng đã thực hiện gửi tự động gần 160 Ngàn Email cho khách hàng. Qua đó cho thấy, tiện ích này được đông đảo khách hàng sử dụng điện quan tâm sử dụng.</p>\r\n\r\n<p><u><strong>Cách thức đăng ký:</strong></u></p>\r\n\r\n<p><u><strong>Bước 1:</strong></u> Khách hàng truy cập Website Chăm sóc khách hàng tại địa chỉ “cskh.hcmpc.vn”. Sau đó đăng nhập để xem thông tin khách hàng (Tên tài khoản là mã khách hàng, mật khẩu mặc định: cmis2012)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://cskh.hcmpc.com.vn/public/img/images/1%281%29.png" style="height:380px; width:678px" /></p>\r\n\r\n<p><u><strong>Bước 2:</strong></u> Chọn mục “Hóa đơn điện tử” thì sẽ xuất hiện bảng thông báo tự động Pop up hướng dẫn khách hàng đăng ký bằng cách nhập mã khách hàng và địa chỉ Email nhận HĐĐT.</p>\r\n\r\n<p style="text-align:center"><img alt="" src="http://cskh.hcmpc.com.vn/public/img/images/2%281%29.png" style="height:423px; width:678px" /></p>\r\n\r\n<p>Với các thao tác đơn giản, khách hàng có thể dễ dàng đăng ký nhận HĐĐT tự động sau khi đã thanh toán tiền điện, tăng thêm tiện ích, thuận tiện hơn cho khách hàng trong việc nhận HĐĐT để quyết toán thuế.</p>\r\n\r\n<p>Góp phần nâng cao chất lượng dịch vụ chăm sóc khách hàng của Tổng &nbsp;công ty Điện lực TP.HCM.</p>\r\n', '2015-03-21', '2.PNG', 1),
(16, 'NV0001', 'abc', 'Hôm nay mình sẽ viết một bài tổng quan về Node.js, vừa là sơ lược về tiểu sử ra đời, vừa là demo khả năng công nghệ của nó. Hi vọng với bài viết này, bạn sẽ hiểu thêm về Node.js cũng như yêu thích nó. Good day!', '<p><span style="color:rgb(0, 0, 0); font-family:tahoma,arial">Hello Noder! Hôm nay mình sẽ viết một bài tổng quan về Node.js, vừa là sơ lược về tiểu sử ra đời, vừa là demo khả năng công nghệ của nó. Hi vọng với bài viết này, bạn sẽ hiểu thêm về Node.js cũng như yêu thích nó. Good day!&nbsp;</span><br />\r\n<span style="color:rgb(0, 0, 0); font-family:tahoma,arial">Để hiểu bài viết này, bạn cần có kiến thức lập trình javascript căn bản, PHP căn bản, HTML căn bản và ... căn bản.&nbsp;</span><br />\r\n<br />\r\n<br />\r\n<strong>Tại sao dùng NodeJS</strong><span style="color:rgb(0, 0, 0); font-family:tahoma,arial">&nbsp;</span><br />\r\n<span style="color:rgb(0, 0, 0); font-family:tahoma,arial">Có nhiều lý do dẫn đến việc ra đời của Node.js, nhưng lý do căn bản nhất vẫn là: Sự phát triển chóng mặt của các thiết bị di động thông minh, kèm theo đó là sự xuất hiện của HTML5, CSS3, Webworker, v.v... Các ứng dụng web trở nên thông minh hơn, nhanh hơn, trực tuyến hơn, đa luồng hơn. Hiển nhiên, với những yêu cầu đặc biệt từ phía client như vậy, server phải thích ứng. Những nỗ lực đa luồng hóa, trực tuyến hóa của ASP.NET, PHP, JSP đã bước đầu mang lại thành công. Nhưng chúng, với tư cách là những ngôn ngữ server gặp một rào cản không thể tránh khỏi: Sự tương thích với Client.&nbsp;</span><br />\r\n<span style="color:rgb(0, 0, 0); font-family:tahoma,arial">Dường như Server và Client không thực sự gắn kết, khi Server sẽ làm nhiệm vụ generate ra các đoạn mã mà client sẽ sử dụng. Server-side-script đóng vai như "Đấng Sáng Tạo" cho client. Mọi thứ mà client cần, chỉ đơn giản là do Server tạo ra, nếu bạn nhìn code từ phía client, hẳn bạn sẽ thấy ngay một điều thú vị: Tất cả những thứ tôi cần, bỗng dưng hiện ra trong code:&nbsp;</span><br />\r\n&nbsp;</p>\r\n', '2015-03-22', '1.png', 1);

